import React, { useContext, Component } from 'react'
import { Stack, Box, Text, Image, Badge, Flex, Avatar, SimpleGrid } from '@chakra-ui/core'
import { MyContext } from '../context'
import SERVICE_TRABAJO from '../services/trabajo'
import trabajo from '../pages/trabajo'
import { Link } from "react-router-dom";

export default class TrabajoDetail extends Component {

    state = {
        trabajos:[]
    }


    async getProjectsInfo(){
        const { trabajos } = await SERVICE_TRABAJO()
        this.setState({
            trabajos
        })
    }


    componentDidMount(){
        console.log("Mount")  


        SERVICE_TRABAJO.getAllTrabajos()

        .then( ( {data} ) => {
            this.setState(
            {
                trabajos: data 

            }
            )
        } )
        .catch(err => console.error(err))

    }


    render(){
        return(
        <>
            <h1>Cosas para mostrar</h1>
            <SimpleGrid minChildWidth="250px" spacing={8}>
                {! this.state.trabajos && <Text>No hay nada </Text>  }

             { this.state.trabajos && this.state.trabajos.map((trabajo ,idx ) => (
                    <li key={idx}>
                        <Link   to= {`/trabajo/${trabajo._id}`}    ></Link>
                    </li>
                ))
            }

            </SimpleGrid>


        </>
        )
    }
}
