import React, { useEffect, useContext } from 'react'
import { MyContext } from '../../context'
// import {
//   Stack,
//   Heading,
//   Box,
//   Flex,
//   FormControl,
//   InputGroup,
//   InputLeftAddon,
//   Input,
//   Icon,
//   useToast
// } from '@chakra-ui/core'
import TrabajoDetail from '../../components/TrabajoDetail'


export default function trabajo({ history }) {
  const context = useContext(MyContext)
  useEffect(() => {
    if (!context.state.isLogged) return history.push('/login')
  })
  return (
    <MyContext.Consumer>
      {context => {
        const { isLogged, loggedUser } = context.state
        if (isLogged)
          return (
            <React.Fragment>
              <Flex
                w="100vw"
                minH="100vh"
                wrap="wrap"
                direction="column"
                align="start"
                mt="5vh"
                ml="5vw"
              >
                <Box>
                  <Heading mt="5vh">Configuraci√≥n:</Heading>
                  <Stack spacing={8} mt="5vh" mr="5vw" mb="3vh" ml="5vw" align="center">
                    <Box p={5} shadow="md" borderWidth="1px" flex="1" rounded="md">
                      <Text fontSize="md">Actualizar foto de perfil.</Text>
                      <Input type="file"  onChange={context.uploadPhoto}  accept="image/*" name="photoURL" multiple = {false} />
                      <Box>
                        <Image
                        rounded="full"
                        backgroundColor="#000"
                        size="150px"/>
                      </Box>
                    </Box>
                  </Stack>
                </Box>
              </Flex>
            </React.Fragment>
          )
        else return <>Loading...</>
      }}
    </MyContext.Consumer>
  )
    }
